import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { async, Observable, of } from 'rxjs';
import { ChatState } from 'src/NgRx/states/chat.state';
import * as ChatActions from '../../../NgRx/actions/chat.action'

@Component({
  selector: 'app-chat-page',
  templateUrl: './chat-page.component.html',
  styleUrls: ['./chat-page.component.scss']
})
export class ChatPageComponent implements OnInit{

  chat$: Observable<ChatState>;

  constructor(private store: Store<{chat: ChatState}>){
    this.chat$ = this.store.select('chat');
  }

  ngOnInit(){
    this.store.dispatch(ChatActions.getAllChat());
    this.chat$.subscribe( (data) => {
      // console.log(data);
    });
  }


}
